query:
    query GetTournament($id: ID, $slug: String) {
  tournament(id: $id, slug: $slug) {
    __typename
    id
    slug
    name
    startsAt
    endsAt
    playerCount
    listsVisible
    listsLocked
    tableAmount
    venue
    address
    city
    region
    country
    postalCode
    description
    cancelledAt
    winValue
    lossValue
    drawValue
    byeValue
    isGalacticConquest
    primaryOrganizer {
      id
      name
    }
    tournamentLinks {
      id
      url
      name
    }
    tournamentRequests {
      id
      status
      group {
        id
      }
      requestingUser {
        id
        name
      }
    }
    groups {
      __typename
      id
      name
      signUpMethod
      playerLimit
      roundAmount
      tableAmount
      topCut
      isCompleted
      nextGroup {
        id
        name
      }
      groupRequests {
        id
        requestingUser {
          id
          name
        }
        status
      }
      groupTiebreakers {
        index
        tiebreaker {
          id
          name
        }
      }
      groupTimer {
        id
        duration
        firstStartedAt
        startedAt
        pausedAt
        endsAt
      }
      nextGroup {
        id
        name
      }
      groupPlayerResults {
        rank
        tournamentPoints
        strengthOfSchedule
        armyPointsDefeated
        wonMatchesAmount
        lostMatchesAmount
        byeMatchesAmount
        drawMatchesAmount
        totalMatches
        matchPointTotal
        player {
          ...playerFields
        }
      }
      players {
        ...playerFields
      }
      rounds {
        id
        isVisible
        roundNumber
        group {
          id
          tableAmount
          tournament {
            id
            primaryOrganizer {
              id
              name
            }
          }
        }
        matches {
          __typename
          id
          tableNumber
          isBye
          isDraw
          playerOne {
            id
            wonMatches {
              id
            }
            lostMatches {
              id
            }
            user {
              id
              name
            }
            groupPlayerResults {
              ...matchPlayerGroupFields
            }
          }
          playerTwo {
            id
            wonMatches {
              id
            }
            lostMatches {
              id
            }
            user {
              id
              name
            }
            groupPlayerResults {
              ...matchPlayerGroupFields
            }
          }
          playerOnePoints
          playerTwoPoints
          playerOneArmyPointsLost
          playerTwoArmyPointsLost
          verifier {
            id
            name
          }
          reporter {
            id
          }
          winner {
            id
            user {
              id
              name
            }
          }
          loser {
            id
          }
          bluePlayer {
            id
            user {
              id
              name
            }
          }
          redPlayer {
            id
            user {
              id
              name
            }
          }
          condition {
            id
            name
          }
          deployment {
            id
            name
          }
          objective {
            id
            name
          }
        }
      }
    }
  }
}

    fragment playerFields on Player {
  id
  isDropped
  isDisqualified
  isCheckedIn
  rank
  tournamentPoints
  strengthOfSchedule
  extendedStrengthOfSchedule
  marginOfVictory
  extendedMarginOfVictory
  legionList {
    id
    faction
  }
  wonMatches {
    id
  }
  lostMatches {
    id
  }
  user {
    id
    name
  }
}


    fragment matchPlayerGroupFields on GroupPlayerResult {
  group {
    id
  }
  wonMatchesAmount
  lostMatchesAmount
  drawMatchesAmount
  byeMatchesAmount
}
