query:
    query LegionLists($playerIds: [ID!]!) {
  legionLists(playerIds: $playerIds) {
    id
    name
    activations
    points
    faction
    listLink
    publicListLink
    listWithHiddenInfo
    player {
      id
    }
    battlefieldCards {
      card {
        name
        type
        subType
      }
    }
    commandCards {
      card {
        name
        type
        subType
      }
    }
    units {
      card {
        name
        title
        rank
        cost
      }
      upgrades {
        card {
          name
          cost
        }
        isInLoadout
      }
    }
    battleForce {
      name
    }
  }
}
